/* eslint-disable @typescript-eslint/no-explicit-any */
import {
  Admin,
  Resource,
} from "react-admin";

import { Dashboard } from "./components/Dashboard";
import authProvider from "./authProvider";
import { dataProvider } from "./dataProvider";
import { LabelList, LabelShow, LabelCreate, LabelEdit } from "./components/labels";
import { TaskStatusCreate, TaskStatusList, TaskStatusShow, TaskStatusEdit } from "./components/taskStatuses";
import { UserCreate, UserList, UserShow, UserEdit } from "./components/users";
import { TaskEdit, TaskList, TaskCreate, TaskShow } from "./components/tasks";

const App = () => (
  <Admin
    dashboard={Dashboard}
    authProvider={authProvider}
    dataProvider={dataProvider}
    // requireAuth
  >

    <Resource
      name="tasks"
      create={TaskCreate}
      list={TaskList}
      show={TaskShow}
      edit={TaskEdit}
    />

    <Resource
      name="users"
      recordRepresentation="email"
      create={UserCreate}
      list={UserList}
      show={UserShow}
      edit={UserEdit}
    />

    <Resource
      name="labels"
      create={LabelCreate}
      list={LabelList}
      show={LabelShow}
      edit={LabelEdit}
    />

    <Resource
      name="task_statuses"
      create={TaskStatusCreate}
      list={TaskStatusList}
      show={TaskStatusShow}
      edit={TaskStatusEdit}
    />
  </Admin>
);

export default App;

