import {
  Create,
  Datagrid,
  DateField,
  Edit,
  List,
  Show,
  SimpleForm,
  SimpleShowLayout,
  TextField,
  TextInput,
} from "react-admin";

export const TaskStatusCreate = () => (
  <Create>
    <SimpleForm>
      {/* <ReferenceInput source="userId" reference="users" /> */}
      <TextInput source="name" />
      <TextInput source="slug" />
    </SimpleForm>
  </Create>
);

export const TaskStatusEdit = () => (
  <Edit>
    <SimpleForm>
      <TextInput source="name" />
      <TextInput source="slug" />
    </SimpleForm>
  </Edit>
);

export const TaskStatusList = () => (
  <List>
    <Datagrid rowClick="edit">
      <TextField source="id" />
      <TextField source="name" />
      <TextField source="slug" />
      <DateField source="createdAt" showTime />
    </Datagrid>
  </List>
);

export const TaskStatusShow = () => (
  <Show>
    <SimpleShowLayout>
      <TextField source="id" />
      <TextField source="name" />
      <DateField source="createdAt" showTime />
    </SimpleShowLayout>
  </Show>
);


