import { Draggable } from "@hello-pangea/dnd";
import { Box, Card, CardActions, CardContent, Typography } from "@mui/material";

import { EditButton, ShowButton } from "react-admin";
import type { Task } from ".";

export const TaskCard = ({ task, index }: { task: Task; index: number }) => {
  return (
    <Draggable draggableId={String(task.id)} index={index}>
      {(provided, snapshot) => (
        <Box
          sx={{ marginBottom: 1 }}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          ref={provided.innerRef}
        >
          <Card
            style={{
              opacity: snapshot.isDragging ? 0.9 : 1,
              transform: snapshot.isDragging ? "rotate(-2deg)" : "",
            }}
            elevation={snapshot.isDragging ? 3 : 1}
          >
            <CardContent>
              <Typography variant="h5" component="div">
                {task.title}
              </Typography>
              <Typography variant="body2">{task.content}</Typography>
              <Typography sx={{ fontSize: 10 }} color="text.secondary">
                {`Index: ${task.index}`}
              </Typography>
            </CardContent>
            <CardActions>
              <EditButton resource="tasks" record={task} />
              <ShowButton resource="tasks" record={task} />
            </CardActions>
          </Card>
        </Box>
      )}
    </Draggable>
  );
};
