import {
  DateField,
  Edit,
  ReferenceField,
  SimpleForm,
  SimpleShowLayout,
  TextField,
  SingleFieldList,
  ChipField,
  ReferenceArrayField,
} from "react-admin";

export const TaskShow = () => (
  <Edit>
    <SimpleShowLayout>
      <TextField source="id" />
      <DateField source="createdAt" />
    </SimpleShowLayout>
    <SimpleForm sx={{ maxWidth: 600 }}>
      <ReferenceField label="Assignee" source="assignee_id" reference="users" />
      {/* <NumberInput source="index" /> */}
      <TextField source="title" fullWidth />
      <TextField source="content" fullWidth multiline />
      <ReferenceField fullWidth source="status" reference="task_statuses" />
      <ReferenceArrayField fullWidth source="taskLabelIds" reference="labels" label="name">
        <SingleFieldList>
          <ChipField source="name" />
        </SingleFieldList>
      </ReferenceArrayField>
      {/* <DateInput source="createdAt" /> */}
    </SimpleForm>
  </Edit>
);
